import axios from 'axios';
/**
 * @import * from '@/src/typedef';
 */

export default class File {
  /**
   * @private
   * @param {Api} api
   */
  constructor(api) {
    /**
     * @private
     * @type {Api}
     * */
    this.api = api;
    /**
     * @private
     * @type {Boolean}
     */
    this.isAdmin = false;
  }

  /**
   * Upload File
   * @param {String} bucketName
   * @param {File} file
   * @param {Object} extra
   * @param {Function} progress
   * @param {axios.cancelToken} [cancelToken]
   * @returns {Promise<String|Error>}
   */
  upload(bucketName, file, extra = {}, progress = e => {}, cancelToken = null) {
    return new Promise((resolve, reject) => {
      let url = `${this.api.config.domain}/files/${encodeURIComponent(
        bucketName
      )}`;
      let data = new FormData();
      let headers = this.api.getHeader(this.isAdmin);

      data.append('extra', JSON.stringify(extra));
      data.append('file', file);
      // headers = Object.assign(headers, data.getHeaders());

      axios
        .post(url, data, {
          headers: headers,
          onUploadProgress: progress,
          cancelToken
        })
        .then(
          res => {
            if (res.status >= 200 && res.status < 300 && res.data.RC === 0) {
              resolve(
                `${this.api.config.domain}/files/${bucketName}/${res.data.result.id}`
              );
            } else {
              resolve(res.data);
            }
          },
          error => {
            this.api.onError(error);
            reject(error);
          }
        );
    });
  }

  /**
   * Get File metadata
   * @param {String} bucketName
   * @param {String} fileId
   * @returns {Promise<{AcceptRanges: String, LastModified: String, ContentLength: Number, ETag: String, ContentType: String, Metadata: Object[], extra: Object[]|Error>}
   */
  getMetadata(bucketName, fileId) {
    return new Promise((resolve, reject) => {
      let url = `${this.api.config.domain}/files/${encodeURIComponent(
        bucketName
      )}/${fileId}/metadata`;
      let data = {};
      let headers = this.api.getHeader(this.isAdmin);

      axios
        .get(url, {
          params: data,
          headers: headers
        })
        .then(
          res => {
            if (res.status >= 200 && res.status < 300 && res.data.RC === 0) {
              resolve(res.data.result);
            }
          },
          error => {
            this.api.onError(error);
            reject(error);
          }
        );
    });
  }

  /**
   * 取得檔案原始路徑
   * @param {String} bucketName
   * @param {String} fileId
   * @param {Object} appendHeaders
   * @returns {Promise<{url: String|Error>}
   */
  getFileSignedUrl(bucketName, fileId, appendHeaders = {}) {
    return new Promise((resolve, reject) => {
      let url = `${this.api.config.domain}/files/${encodeURIComponent(
        bucketName
      )}/${fileId}`;
      let data = {
        signedUrlOnly: 1
      };
      let headers = this.api.getHeader(this.isAdmin);

      headers = Object.assign(headers, appendHeaders);

      axios
        .get(url, {
          params: data,
          headers: headers
        })
        .then(
          res => {
            if (res.status >= 200 && res.status < 300) {
              resolve(res.data.result.url);
            }
          },
          error => {
            this.api.onError(error);
            reject(error);
          }
        );
    });
  }

  /**
   * Downliad File
   * @param {String} bucketName
   * @param {String} fileId
   * @param {Object} appendHeaders
   * @param {Function} progress
   */
  download(bucketName, fileId, appendHeaders = {}, progress = e => {}) {
    return new Promise((resolve, reject) => {
      let url = `${this.api.config.domain}/files/${encodeURIComponent(
        bucketName
      )}/${fileId}`;
      let data = {
        signedUrlOnly: 1
      };
      let headers = this.api.getHeader(this.isAdmin);

      headers = Object.assign(headers, appendHeaders);

      axios
        .get(url, {
          params: data,
          headers: headers
        })
        .then(
          res => {
            if (res.status >= 200 && res.status < 300) {
              try {
                this.downloadFileFormRealUrl(
                  res.data.result.url,
                  appendHeaders,
                  progress
                ).then(resFile => {
                  resolve(resFile);
                });
              } catch (e) {
                reject(e);
              }
            }
          },
          error => {
            this.api.onError(error);
            reject(error);
          }
        );
    });
  }
  /**
   * Downliad File
   * @private
   * @param {String} url
   * @param {Object} appendHeaders
   * @param {Function} progress
   */
  downloadFileFormRealUrl(url, appendHeaders = {}, progress = e => {}) {
    return new Promise((resolve, reject) => {
      let data = {};
      let headers = this.api.getHeader(this.isAdmin);

      headers = Object.assign(headers, appendHeaders);

      axios
        .get(url, {
          params: data,
          headers: headers,
          onDownloadProgress: progress,
          responseType: 'arraybuffer'
        })
        .then(
          res => {
            if (res.status >= 200 && res.status < 300) {
              resolve(res);
            }
          },
          error => {
            this.api.onError(error);
            reject(error);
          }
        );
    });
  }
}

import Admin from './admin';
import Auth from './auth';
import File from './file';
import Me from './me';
import Message from './message';
import Room from './room';
import Search from './search';
import Url from './url';
import lib from '../lib';
/**
 * @import * from '@/src/typedef';
 */

export default class IMKitApi {
  /**
   * 建立 API 物件
   * @param {ApiConfig} config
   */
  constructor(config) {
    /** @type {ApiConfig} */
    this.config = config;
    /** @type {Admin} */
    this.admin = new Admin(this);
    /** @type {Auth} */
    this.auth = new Auth(this);
    /** @type {File} */
    this.file = new File(this);
    /** @type {Me} */
    this.me = new Me(this);
    /** @type {Message} */
    this.message = new Message(this);
    /** @type {Room} */
    this.room = new Room(this);
    /** @type {Search} */
    this.search = new Search(this);
    /** @type {Url} */
    this.url = new Url(this);
  }

  /**
   * @private
   * @param {Boolean} isAdmin
   */
  getHeader(isAdmin = false) {
    let header;

    if (isAdmin) {
      header = {
        API_KEY: this.config.apiKey
      };
    } else {
      header = {
        'IM-Authorization': this.config.token,
        CLIENT_KEY: this.config.clientKey
      };
    }
    return header;
  }

  /**
   * @private
   * @param {Object} error
   */
  onError(error) {
    if (error.response) {
      if (error.response.data.RC) {
        this.config.events.onAjaxError(error.response.data);
      } else {
        this.config.events.onAjaxError({
          RC: error.response.status,
          RM: error.response.statusText
        });
      }
    }
  }

  /**
   * 產生 RSA 金鑰，暫不支援 node.js
   * @returns {Promise<{publicKey: String, privateKey: String}|Error>}
   */
  generateRsaKey() {
    return lib.message.generateRsaKey();
  }
}

